  }

  .bg-gold\/20 {
    background-color: hsl(43, 96%, 56%, 0.2);
  }

  .bg-silver\/20 {
    background-color: hsl(220, 9%, 75%, 0.2);
  }

  .bg-bronze\/20 {
    background-color: hsl(25, 95%, 53%, 0.2);
  }

  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }
  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  /* Remove scrollbar no desktop, manter no mobile */
  @media (min-width: 768px) {
    body::-webkit-scrollbar {
      display: none;
    }
    body {
      -ms-overflow-style: none;
      scrollbar-width: none;
    }
  }

  /* Manter scrollbar visÃ­vel no mobile para facilitar rolamento */
  @media (max-width: 767px) {
    body::-webkit-scrollbar {
      width: 4px;
    }
    body::-webkit-scrollbar-track {
      background: transparent;
    }
    body::-webkit-scrollbar-thumb {
      background: rgba(255, 255, 255, 0.3);
      border-radius: 2px;
    }
  }

  .line-clamp-2 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
  }
}

