import memoizee from 'memoizee';

// Cache configuration
const CACHE_TTL = 60 * 1000; // 60 seconds
const CACHE_MAX_SIZE = 100;

// Create memoized function with TTL
export function createCachedFunction<T extends (...args: any[]) => Promise<any>>(
  fn: T,
  options?: {
    ttl?: number;
    maxSize?: number;
    keyGenerator?: (...args: Parameters<T>) => string;
  }
): T {
  return memoizee(fn, {
    promise: true,
    maxAge: options?.ttl || CACHE_TTL,
    max: options?.maxSize || CACHE_MAX_SIZE,
    normalizer: options?.keyGenerator || JSON.stringify,
    preFetch: 0.5, // Prefetch when 50% of TTL has passed
  }) as T;
}

// Cache manager for manual control
export class CacheManager {
  private static caches: Map<string, any> = new Map();

  static register(key: string, cachedFunction: any): void {
    this.caches.set(key, cachedFunction);
  }

  static clear(key?: string): void {
    if (key) {
      const cache = this.caches.get(key);
      if (cache?.clear) cache.clear();
    } else {
      // Clear all caches
      this.caches.forEach(cache => {
        if (cache?.clear) cache.clear();
      });
    }
  }

  static getCacheStats(key: string): any {
    const cache = this.caches.get(key);
    if (cache?.statistics) {
      return cache.statistics();
    }
    return null;
  }
}

// Performance monitoring
export function measurePerformance(name: string) {
  return function (target: any, propertyKey: string, descriptor: PropertyDescriptor) {
    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      const start = performance.now();
      try {
        const result = await originalMethod.apply(this, args);
        const duration = performance.now() - start;

        if (duration > 100) { // Log slow operations
          console.warn(`[PERF] ${name}.${propertyKey} took ${duration.toFixed(2)}ms`);
        }

        return result;
      } catch (error) {
        const duration = performance.now() - start;
        console.error(`[PERF] ${name}.${propertyKey} failed after ${duration.toFixed(2)}ms`);
        throw error;
      }
    };

    return descriptor;
  };
}

server/utils/commission-calculator.ts.content:
import type { Sale, Attendant } from "@shared/schema";
import type { CommissionRule } from "@shared/commission-schema";

export interface CommissionCalculation {
  saleValue: number;
  commissionValue: number;
  rule: CommissionRule;
  breakdown: string;
}

export function calculateCommission(
  sale: Sale,
  attendant: Attendant,
  rules: CommissionRule[]
): CommissionCalculation | null {
  const saleValue = parseFloat(sale.value);

  // Find applicable rule
  const applicableRule = rules.find(rule => {
    if (rule.isActive !== 1) return false;

    if (rule.minTarget && rule.maxTarget) {
      const minTarget = parseFloat(rule.minTarget);
      const maxTarget = parseFloat(rule.maxTarget);
      return saleValue >= minTarget && saleValue <= maxTarget;
    }

    return true; // Default rule
  });

  if (!applicableRule) return null;

  let commissionValue = 0;
  let breakdown = "";

  switch (applicableRule.type) {
    case "percentage":
      commissionValue = saleValue * (parseFloat(applicableRule.baseValue) / 100);
      breakdown = `${applicableRule.baseValue}% de R$ ${saleValue.toFixed(2)}`;
      break;

    case "fixed":
      commissionValue = parseFloat(applicableRule.baseValue);
      breakdown = `Valor fixo de R$ ${commissionValue.toFixed(2)}`;
      break;

    case "tiered":
      // Base commission
      const baseCommission = saleValue * (parseFloat(applicableRule.baseValue) / 100);
      commissionValue = baseCommission;
      breakdown = `Base: ${applicableRule.baseValue}% = R$ ${baseCommission.toFixed(2)}`;

      // Check for bonus
      if (applicableRule.minTarget && saleValue >= parseFloat(applicableRule.minTarget)) {
        const bonus = saleValue * (parseFloat(applicableRule.bonusPercentage || "0") / 100);
        commissionValue += bonus;
        breakdown += ` + BÃ´nus: ${applicableRule.bonusPercentage}% = R$ ${bonus.toFixed(2)}`;
      }
      break;
  }

  // Check monthly performance bonus
  const monthlyEarnings = parseFloat(attendant.earnings);
  if (monthlyEarnings > 10000) {
    const performanceBonus = commissionValue * 0.1; // 10% extra
    commissionValue += performanceBonus;
    breakdown += ` + BÃ´nus performance: R$ ${performanceBonus.toFixed(2)}`;
  }

  return {
    saleValue,
    commissionValue,
    rule: applicableRule,
    breakdown
  };
}

export function calculatePeriodCommissions(
  sales: Sale[],
  attendants: Map<number, Attendant>,
  rules: CommissionRule[],
  startDate: Date,
  endDate: Date
): Map<number, { total: number; count: number; details: CommissionCalculation[] }> {
  const commissionsByAttendant = new Map<number, { total: number; count: number; details: CommissionCalculation[] }>();

  const periodSales = sales.filter(sale => {
    const saleDate = new Date(sale.createdAt);
    return saleDate >= startDate && saleDate <= endDate;
  });

  periodSales.forEach(sale => {
    const attendant = attendants.get(sale.attendantId);
    if (!attendant) return;

    const calculation = calculateCommission(sale, attendant, rules);
    if (!calculation) return;

    const current = commissionsByAttendant.get(sale.attendantId) || { total: 0, count: 0, details: [] };
    current.total += calculation.commissionValue;
    current.count += 1;
    current.details.push(calculation);

    commissionsByAttendant.set(sale.attendantId, current);
  });

  return commissionsByAttendant;
}

server/utils/reports.ts.content:
import type { Sale, Attendant } from "@shared/schema";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";

export interface SalesReport {
  period: string;
  totalSales: number;
  totalValue: number;
  averageTicket: number;
  topAttendants: Array<{
    name: string;
    sales: number;
    value: number;
  }>;
  salesByDay: Array<{
    date: string;
    sales: number;
    value: number;
  }>;
}

export function generateSalesReport(
  sales: Sale[], 
  attendants: Attendant[],
  startDate: Date,
  endDate: Date
): SalesReport {
  // Filter sales by period
  const filteredSales = sales.filter(sale => {
    const saleDate = new Date(sale.createdAt);
    return saleDate >= startDate && saleDate <= endDate;
  });

  // Calculate totals
  const totalSales = filteredSales.length;
  const totalValue = filteredSales.reduce((sum, sale) => sum + parseFloat(sale.value), 0);
  const averageTicket = totalSales > 0 ? totalValue / totalSales : 0;

  // Group by attendant
  const salesByAttendant = new Map<number, { sales: number; value: number }>();
  filteredSales.forEach(sale => {
    const current = salesByAttendant.get(sale.attendantId) || { sales: 0, value: 0 };
    salesByAttendant.set(sale.attendantId, {
      sales: current.sales + 1,
      value: current.value + parseFloat(sale.value)
    });
  });

  // Top attendants
  const topAttendants = Array.from(salesByAttendant.entries())
    .map(([attendantId, data]) => {
      const attendant = attendants.find(a => a.id === attendantId);
      return {
        name: attendant?.name || "Desconhecido",
        sales: data.sales,
        value: data.value
      };
    })
    .sort((a, b) => b.value - a.value)
    .slice(0, 10);

  // Sales by day
  const salesByDay = new Map<string, { sales: number; value: number }>();
  filteredSales.forEach(sale => {
    const date = format(new Date(sale.createdAt), "yyyy-MM-dd");
    const current = salesByDay.get(date) || { sales: 0, value: 0 };
    salesByDay.set(date, {
      sales: current.sales + 1,
      value: current.value + parseFloat(sale.value)
    });
  });

  const salesByDayArray = Array.from(salesByDay.entries())
    .map(([date, data]) => ({
      date,
      sales: data.sales,
      value: data.value
    }))
    .sort((a, b) => a.date.localeCompare(b.date));

  return {
    period: `${format(startDate, "dd/MM/yyyy", { locale: ptBR })} - ${format(endDate, "dd/MM/yyyy", { locale: ptBR })}`,
    totalSales,
    totalValue,
    averageTicket,
    topAttendants,
    salesByDay: salesByDayArray
  };
}

export function exportToCSV(data: any[], filename: string): string {
  if (data.length === 0) return "";

  // Get headers
  const headers = Object.keys(data[0]);
  const csvHeaders = headers.join(",");

  // Convert data to CSV rows
  const csvRows = data.map(row => {
    return headers.map(header => {
      const value = row[header];
      // Escape quotes and wrap in quotes if contains comma
      if (typeof value === "string" && (value.includes(",") || value.includes('"'))) {
        return `"${value.replace(/"/g, '""')}"`;
      }
      return value;
    }).join(",");
  });

  return [csvHeaders, ...csvRows].join("\n");
}

