                <p>SalesControl Pro v2.0</p>
                <p>Sistema Gamificado de Vendas</p>
                <p>Ãšltima atualizaÃ§Ã£o: {new Date().toLocaleDateString('pt-BR')}</p>
              </div>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

