    const report = `ðŸ“Š RELATÃ“RIO - ${attendant.name}
ðŸ’° Total em Vendas: R$ ${attendant.earnings}
ðŸŽ¯ NÃºmero de Vendas: ${stats.totalSales}
ðŸ† Conquistas: ${stats.totalAchievements}
ðŸ“… Cadastrado em: ${new Date(attendant.createdAt).toLocaleDateString('pt-BR')}`;

    if (navigator.share) {
