import { useState } from "react";
import Header from "@/components/Header";
import Navigation from "@/components/Navigation";
import { DashboardAdvanced } from "@/components/DashboardAdvanced";
import PageLoader from "@/components/PageLoader";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Download, FileSpreadsheet, Calendar, TrendingUp, DollarSign, Users } from "lucide-react";
import { useQuery } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { Sale, Attendant } from "@shared/schema";
import { format } from "date-fns";
import { ptBR } from "date-fns/locale";

export default function DashboardPage() {
  const [isExporting, setIsExporting] = useState(false);
  const { toast } = useToast();

  // Fetch data
  const { data: sales = [], isLoading: loadingSales } = useQuery<Sale[]>({
    queryKey: ["/api/sales"],
  });

  const { data: attendants = [], isLoading: loadingAttendants } = useQuery<Attendant[]>({
    queryKey: ["/api/attendants"],
  });

  const isLoading = loadingSales || loadingAttendants;

  const handleExportCSV = async (type: 'sales' | 'attendants' | 'report') => {
    setIsExporting(true);

    try {
      let data: any[] = [];
      let filename = '';

      switch (type) {
        case 'sales':
          data = sales.map(sale => {
            const attendant = attendants.find(a => a.id === sale.attendantId);
            return {
              'Data': format(new Date(sale.createdAt), "dd/MM/yyyy HH:mm", { locale: ptBR }),
              'Atendente': attendant?.name || 'Desconhecido',
              'Valor': `R$ ${sale.value}`,
              'Cliente': sale.clientName || '-',
              'Telefone': sale.clientPhone || '-',
              'Email': sale.clientEmail || '-'
            };
          });
          filename = `vendas_${format(new Date(), "yyyy-MM-dd")}.csv`;
          break;

        case 'attendants':
          data = attendants.map(att => ({
            'Nome': att.name,
            'Faturamento': `R$ ${att.earnings}`,
            'Vendas': sales.filter(s => s.attendantId === att.id).length,
            'Ticket MÃ©dio': `R$ ${(parseFloat(att.earnings) / sales.filter(s => s.attendantId === att.id).length || 0).toFixed(2)}`
          }));
          filename = `atendentes_${format(new Date(), "yyyy-MM-dd")}.csv`;
          break;

        case 'report':
          const totalRevenue = sales.reduce((sum, sale) => sum + parseFloat(sale.value), 0);
          const totalSales = sales.length;
          const averageTicket = totalSales > 0 ? totalRevenue / totalSales : 0;

          data = [{
            'PerÃ­odo': `01/${format(new Date(), "MM/yyyy")} - ${format(new Date(), "dd/MM/yyyy")}`,
            'Total de Vendas': totalSales,
            'Faturamento Total': `R$ ${totalRevenue.toFixed(2)}`,
            'Ticket MÃ©dio': `R$ ${averageTicket.toFixed(2)}`,
            'Atendentes Ativos': attendants.length
          }];

          // Add daily breakdown
          const dailyData = new Map<string, { count: number; value: number }>();
          sales.forEach(sale => {
            const date = format(new Date(sale.createdAt), "dd/MM/yyyy");
            const current = dailyData.get(date) || { count: 0, value: 0 };
            current.count++;
            current.value += parseFloat(sale.value);
            dailyData.set(date, current);
          });

          dailyData.forEach((data, date) => {
            data.push({
              'Data': date,
              'Vendas': data.count,
              'Faturamento': `R$ ${data.value.toFixed(2)}`
            } as any);
          });

          filename = `relatorio_${format(new Date(), "yyyy-MM-dd")}.csv`;
          break;
      }

      // Convert to CSV
      if (data.length > 0) {
        const headers = Object.keys(data[0]).join(',');
        const rows = data.map(row => 
          Object.values(row).map(value => 
            typeof value === 'string' && value.includes(',') ? `"${value}"` : value
          ).join(',')
        );
        const csv = [headers, ...rows].join('\n');

        // Download file
        const blob = new Blob(['\ufeff' + csv], { type: 'text/csv;charset=utf-8;' });
        const link = document.createElement('a');
        link.href = URL.createObjectURL(blob);
        link.download = filename;
        link.click();

        toast({
          title: "ExportaÃ§Ã£o concluÃ­da!",
          description: `Arquivo ${filename} baixado com sucesso.`,
        });
      }
    } catch (error) {
      toast({
        title: "Erro na exportaÃ§Ã£o",
        description: "NÃ£o foi possÃ­vel exportar os dados.",
        variant: "destructive",
      });
    } finally {
      setIsExporting(false);
    }
  };

  if (isLoading) {
    return <PageLoader message="Carregando dashboard..." />;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary-dark via-primary-dark to-secondary-dark/50">
      <Header />
      <Navigation />

      <main className="mobile-safe max-w-7xl py-4 sm:py-6 lg:py-8">
        {/* Header */}
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6 gap-4">
          <div>
            <h1 className="text-3xl font-bold text-primary-light flex items-center gap-3">
              <TrendingUp className="text-success" size={36} />
              Dashboard de Vendas
            </h1>
            <p className="text-secondary-light mt-1">
              Acompanhe o desempenho da sua equipe em tempo real
            </p>
          </div>

          <div className="flex flex-wrap gap-2">
            <Button
              onClick={() => handleExportCSV('sales')}
              disabled={isExporting}
              className="bg-info text-white hover:bg-info/90"
              size="sm"
            >
              <FileSpreadsheet size={16} className="mr-2" />
              Exportar Vendas
            </Button>
            <Button
              onClick={() => handleExportCSV('attendants')}
              disabled={isExporting}
              className="bg-success text-white hover:bg-success/90"
              size="sm"
            >
              <Download size={16} className="mr-2" />
              Exportar Atendentes
            </Button>
            <Button
              onClick={() => handleExportCSV('report')}
              disabled={isExporting}
              className="bg-warning text-white hover:bg-warning/90"
              size="sm"
            >
              <Calendar size={16} className="mr-2" />
              RelatÃ³rio Completo
            </Button>
          </div>
        </div>

        {/* Dashboard Component */}
        <DashboardAdvanced />

        {/* Quick Actions */}
        <Card className="mt-6 bg-card border-border">
          <CardHeader>
            <CardTitle className="text-lg font-semibold">AÃ§Ãµes RÃ¡pidas</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <Button
                variant="outline"
                className="justify-start"
                onClick={() => window.location.href = '/'}
              >
                <DollarSign className="mr-2 h-4 w-4" />
                Registrar Venda
              </Button>
              <Button
                variant="outline"
                className="justify-start"
                onClick={() => window.location.href = '/goals'}
              >
                <Calendar className="mr-2 h-4 w-4" />
                Gerenciar Metas
              </Button>
              <Button
                variant="outline"
                className="justify-start"
                onClick={() => window.location.href = '/attendants'}
              >
                <Users className="mr-2 h-4 w-4" />
                Cadastrar Atendente
              </Button>
            </div>
          </CardContent>
        </Card>
      </main>
    </div>
  );
}

client/src/pages/goals.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Target, Plus, Trophy, Calendar, Clock, TrendingUp, Award, CheckCircle, XCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import Header from "@/components/Header";
import Navigation from "@/components/Navigation";
import type { Goal, Attendant, Achievement } from "@shared/schema";

export default function GoalsPage() {
  const [isNewGoalDialogOpen, setIsNewGoalDialogOpen] = useState(false);
  const [selectedAttendant, setSelectedAttendant] = useState<string>("");
  const [newGoal, setNewGoal] = useState({
    title: "",
    description: "",
    targetValue: "",
    goalType: "",
    startDate: "",
    endDate: "",
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch data
  const { data: goals, isLoading: loadingGoals } = useQuery<Goal[]>({
    queryKey: ["/api/goals"],
  });

  const { data: attendants, isLoading: loadingAttendants } = useQuery<Attendant[]>({
    queryKey: ["/api/attendants"],
  });

  const { data: achievements, isLoading: loadingAchievements } = useQuery<Achievement[]>({
    queryKey: ["/api/achievements"],
  });

  const isLoading = loadingGoals || loadingAttendants || loadingAchievements;

  // Create goal mutation
  const createGoalMutation = useMutation({
    mutationFn: async (goalData: any) => {
      const response = await apiRequest("POST", "/api/goals", {
        attendantId: parseInt(goalData.attendantId),
        title: goalData.title,
        description: goalData.description,
        targetValue: goalData.targetValue,
        goalType: goalData.goalType,
        startDate: new Date(goalData.startDate),
        endDate: new Date(goalData.endDate),
      });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Meta criada com sucesso!",
        description: "A nova meta foi definida para o atendente.",
      });
      setNewGoal({
        title: "",
        description: "",
        targetValue: "",
        goalType: "",
        startDate: "",
        endDate: "",
      });
      setSelectedAttendant("");
      setIsNewGoalDialogOpen(false);
      queryClient.invalidateQueries({ queryKey: ["/api/goals"] });
    },
    onError: () => {
      toast({
        title: "Erro ao criar meta",
        description: "Ocorreu um erro ao criar a meta. Tente novamente.",
        variant: "destructive",
      });
    },
  });

  // Deactivate goal mutation
  const deactivateGoalMutation = useMutation({
    mutationFn: async (goalId: number) => {
      const response = await apiRequest("PUT", `/api/goals/${goalId}/deactivate`, {});
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Meta finalizada!",
        description: "A meta foi marcada como finalizada.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/goals"] });
    },
    onError: () => {
      toast({
        title: "Erro ao finalizar meta",
        description: "Ocorreu um erro ao finalizar a meta.",
        variant: "destructive",
      });
    },
  });

  const handleCreateGoal = () => {
    if (!selectedAttendant || !newGoal.title || !newGoal.targetValue || !newGoal.goalType || !newGoal.startDate || !newGoal.endDate) {
      toast({
        title: "Campos obrigatÃ³rios",
        description: "Por favor, preencha todos os campos obrigatÃ³rios.",
        variant: "destructive",
      });
      return;
    }

    createGoalMutation.mutate({
      ...newGoal,
      attendantId: selectedAttendant,
    });
  };

  const getAttendantName = (attendantId: number) => {
    return attendants?.find(a => a.id === attendantId)?.name || "Desconhecido";
  };

  const getGoalTypeLabel = (type: string) => {
    const types: { [key: string]: string } = {
      daily: "DiÃ¡ria",
      weekly: "Semanal",
      monthly: "Mensal",
      quarterly: "Trimestral",
      yearly: "Anual",
    };
    return types[type] || type;
  };

  const getGoalProgress = (goal: Goal) => {
    const current = parseFloat(goal.currentValue);
    const target = parseFloat(goal.targetValue);
    return target > 0 ? (current / target) * 100 : 0;
  };

  const getGoalStatus = (goal: Goal) => {
    const now = new Date();
    const endDate = new Date(goal.endDate);
    const progress = getGoalProgress(goal);

    if (!goal.isActive) return "Finalizada";
    if (now > endDate) return "Vencida";
    if (progress >= 100) return "ConcluÃ­da";
    return "Em andamento";
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case "ConcluÃ­da": return "bg-white text-green-700 border border-green-300";
      case "Em andamento": return "bg-white text-blue-700 border border-blue-300";
      case "Vencida": return "bg-white text-red-700 border border-red-300";
      case "Finalizada": return "bg-white text-gray-700 border border-gray-300";
      default: return "bg-white text-gray-700 border border-gray-300";
    }
  };

  const formatDate = (date: Date) => {
    return new Intl.DateTimeFormat('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    }).format(new Date(date));
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-primary-dark">
        <Header />
        <Navigation />
        <div className="flex items-center justify-center py-20">
          <Card className="bg-card border-border p-8">
            <CardContent className="flex flex-col items-center gap-4">
              <div className="h-8 w-8 animate-spin rounded-full border-b-2 border-primary"></div>
              <p className="text-secondary-light">Carregando metas...</p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-primary-dark">
      <Header />
      <Navigation />

      <main className="mobile-safe max-w-6xl py-4 sm:py-6 lg:py-8">
        {/* Goals Header */}
        <div className="flex justify-between items-start mb-6">
          <div className="flex items-center gap-3">
            <Target className="text-success" size={32} />
            <div>
              <h2 className="text-2xl font-bold text-primary-light">Metas e Conquistas</h2>
              <p className="text-secondary-light">Defina e acompanhe as metas da sua equipe</p>
            </div>
          </div>

          <Dialog open={isNewGoalDialogOpen} onOpenChange={setIsNewGoalDialogOpen}>
            <DialogTrigger asChild>
              <Button className="bg-success text-primary-light hover:bg-success-dark">
                <Plus size={18} className="mr-2" />
                Nova Meta
              </Button>
            </DialogTrigger>
            <DialogContent className="bg-card border-border text-primary-light max-w-md">
              <DialogHeader>
                <DialogTitle className="text-primary-light">Criar Nova Meta</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="attendant" className="text-secondary-light">Atendente</Label>
                  <Select value={selectedAttendant} onValueChange={setSelectedAttendant}>
                    <SelectTrigger className="bg-input border-border text-primary-light">
                      <SelectValue placeholder="Selecione o atendente" />
                    </SelectTrigger>
                    <SelectContent className="bg-card border-border">
                      {attendants?.map(attendant => (
                        <SelectItem key={attendant.id} value={attendant.id.toString()}>
                          {attendant.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="title" className="text-secondary-light">TÃ­tulo da Meta</Label>
                  <Input
                    id="title"
                    value={newGoal.title}
                    onChange={(e) => setNewGoal({...newGoal, title: e.target.value})}
                    placeholder="Ex: Vendas do mÃªs"
                    className="bg-input border-border text-primary-light placeholder:text-muted-light"
                  />
                </div>

                <div>
                  <Label htmlFor="description" className="text-secondary-light">DescriÃ§Ã£o</Label>
                  <Textarea
                    id="description"
                    value={newGoal.description}
                    onChange={(e) => setNewGoal({...newGoal, description: e.target.value})}
                    placeholder="Descreva a meta..."
                    className="bg-input border-border text-primary-light placeholder:text-muted-light"
                  />
                </div>

                <div>
                  <Label htmlFor="targetValue" className="text-secondary-light">Valor da Meta (R$)</Label>
                  <Input
                    id="targetValue"
                    type="number"
                    step="0.01"
                    value={newGoal.targetValue}
                    onChange={(e) => setNewGoal({...newGoal, targetValue: e.target.value})}
                    placeholder="0.00"
                    className="bg-input border-border text-primary-light placeholder:text-muted-light"
                  />
                </div>

                <div>
                  <Label htmlFor="goalType" className="text-secondary-light">Tipo de Meta</Label>
                  <Select value={newGoal.goalType} onValueChange={(value) => setNewGoal({...newGoal, goalType: value})}>
                    <SelectTrigger className="bg-input border-border text-primary-light">
                      <SelectValue placeholder="Selecione o tipo" />
                    </SelectTrigger>
                    <SelectContent className="bg-card border-border">
                      <SelectItem value="daily">DiÃ¡ria</SelectItem>
                      <SelectItem value="weekly">Semanal</SelectItem>
                      <SelectItem value="monthly">Mensal</SelectItem>
                      <SelectItem value="quarterly">Trimestral</SelectItem>
                      <SelectItem value="yearly">Anual</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="startDate" className="text-secondary-light">Data de InÃ­cio</Label>
                    <Input
                      id="startDate"
                      type="date"
                      value={newGoal.startDate}
                      onChange={(e) => setNewGoal({...newGoal, startDate: e.target.value})}
                      className="bg-input border-border text-primary-light"
                    />
                  </div>
                  <div>
                    <Label htmlFor="endDate" className="text-secondary-light">Data de Fim</Label>
                    <Input
                      id="endDate"
                      type="date"
                      value={newGoal.endDate}
                      onChange={(e) => setNewGoal({...newGoal, endDate: e.target.value})}
                      className="bg-input border-border text-primary-light"
                    />
                  </div>
                </div>

                <Button 
                  onClick={handleCreateGoal}
                  disabled={createGoalMutation.isPending}
                  className="w-full bg-success text-primary-light hover:bg-success-dark"
                >
                  {createGoalMutation.isPending ? "Criando..." : "Criar Meta"}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>

        {/* Goals List */}
        <div className="space-y-4 mb-8">
          {goals?.map((goal) => {
            const progress = getGoalProgress(goal);
            const status = getGoalStatus(goal);

            return (
              <Card key={goal.id} className="bg-card border-border">
                <CardContent className="p-6">
                  <div className="flex items-start justify-between mb-4">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <h3 className="text-xl font-bold text-primary-light">{goal.title}</h3>
                        <Badge className={getStatusColor(status)}>{status}</Badge>
                        <Badge variant="outline" className="bg-white text-blue-700 border border-blue-300">
                          {getGoalTypeLabel(goal.goalType)}
                        </Badge>
                      </div>
                      <p className="text-secondary-light mb-2">{goal.description}</p>
                      <div className="flex items-center gap-4 text-sm text-secondary-light">
                        <div className="flex items-center gap-1">
                          <Calendar size={16} />
                          {formatDate(goal.startDate)} - {formatDate(goal.endDate)}
                        </div>
                        <div className="flex items-center gap-1">
                          <Target size={16} />
                          {getAttendantName(goal.attendantId)}
                        </div>
                      </div>
                    </div>

                    {goal.isActive && (
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => deactivateGoalMutation.mutate(goal.id)}
                        disabled={deactivateGoalMutation.isPending}
                        className="border-danger text-danger hover:bg-danger hover:text-primary-light"
                      >
                        <XCircle size={16} className="mr-1" />
                        Finalizar
                      </Button>
                    )}
                  </div>

                  <div className="space-y-2">
                    <div className="flex justify-between items-center">
                      <span className="text-secondary-light">Progresso</span>
                      <span className="text-primary-light font-semibold">
                        R$ {parseFloat(goal.currentValue).toFixed(2)} / R$ {parseFloat(goal.targetValue).toFixed(2)}
                      </span>
                    </div>
                    <Progress value={progress} className="h-3" />
                    <div className="text-right text-sm text-secondary-light">
                      {progress.toFixed(1)}% concluÃ­do
                    </div>
                  </div>
                </CardContent>
              </Card>
            );
          })}
        </div>

        {/* Recent Achievements */}
        <div className="mb-6">
          <div className="flex items-center gap-3 mb-4">
            <Trophy className="text-gold" size={24} />
            <h3 className="text-xl font-bold text-primary-light">Conquistas Recentes</h3>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {achievements?.slice(0, 6).map((achievement) => (
              <Card key={achievement.id} className="bg-card border-border">
                <CardContent className="p-4">
                  <div className="flex items-center gap-3 mb-3">
                    <div 
                      className="w-10 h-10 rounded-full flex items-center justify-center text-white"
                      style={{ backgroundColor: achievement.badgeColor }}
                    >
                      {achievement.icon === "trophy" && <Trophy size={20} />}
                      {achievement.icon === "target" && <Target size={20} />}
                      {achievement.icon === "award" && <Award size={20} />}
                      {achievement.icon === "check" && <CheckCircle size={20} />}
                    </div>
                    <div className="flex-1">
                      <h4 className="font-semibold text-primary-light">{achievement.title}</h4>
                      <p className="text-sm text-secondary-light">{getAttendantName(achievement.attendantId)}</p>
                    </div>
                  </div>
                  <p className="text-sm text-secondary-light mb-2">{achievement.description}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-xs text-muted-light">
                      {formatDate(achievement.achievedAt)}
                    </span>
                    <div className="flex items-center gap-1 text-gold">
                      <TrendingUp size={14} />
                      <span className="text-sm font-semibold">+{achievement.pointsAwarded} pts</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

client/src/pages/history.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { History, TrendingUp, BarChart3, DollarSign, Calendar, Filter, Plus, User, Phone, Mail, MapPin, Eye, Trash2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import Header from "@/components/Header";
import Navigation from "@/components/Navigation";
import type { Attendant, Sale } from "@shared/schema";

export default function HistoryPage() {
  const [selectedAttendant, setSelectedAttendant] = useState<string>("all");
  const [selectedMonth, setSelectedMonth] = useState<string>("all");
  const [clientFilter, setClientFilter] = useState("");
  const [selectedSale, setSelectedSale] = useState<Sale | null>(null);
  const [isDetailsModalOpen, setIsDetailsModalOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch data
  const { data: attendants, isLoading: loadingAttendants } = useQuery<Attendant[]>({
    queryKey: ["/api/attendants"],
  });

  const { data: sales, isLoading: loadingSales } = useQuery<Sale[]>({
    queryKey: ["/api/sales"],
  });

  const isLoading = loadingAttendants || loadingSales;

  // Calculate summary statistics
  const totalSales = sales?.length || 0;
  const totalValue = sales?.reduce((sum, sale) => sum + parseFloat(sale.value), 0) || 0;
  const averageTicket = totalSales > 0 ? totalValue / totalSales : 0;

  // Filter sales based on selected filters
  const filteredSales = sales?.filter(sale => {
    if (selectedAttendant !== "all" && sale.attendantId !== parseInt(selectedAttendant)) {
      return false;
    }
    // Additional filters can be added here
    return true;
  }) || [];

  // Get attendant name by ID
  const getAttendantName = (attendantId: number) => {
    return attendants?.find(a => a.id === attendantId)?.name || "Desconhecido";
  };

  // Format date
  const formatDate = (date: Date) => {
    return new Intl.DateTimeFormat('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(new Date(date));
  };

  // Delete sale mutation
  const deleteSaleMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await apiRequest("DELETE", `/api/sales/${id}`, {});
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Venda removida!",
        description: "A venda foi removida com sucesso.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/sales"] });
      queryClient.invalidateQueries({ queryKey: ["/api/attendants"] });
    },
    onError: () => {
      toast({
        title: "Erro ao remover venda",
        description: "Ocorreu um erro ao remover a venda.",
        variant: "destructive",
      });
    },
  });

  // Handle view details
  const handleViewDetails = (sale: Sale) => {
    setSelectedSale(sale);
    setIsDetailsModalOpen(true);
  };

  // Handle delete sale
  const handleDeleteSale = (id: number) => {
    if (window.confirm("Tem certeza que deseja remover esta venda?")) {
      deleteSaleMutation.mutate(id);
    }
  };

  // Format detailed date
  const formatDetailedDate = (date: Date) => {
    return new Intl.DateTimeFormat('pt-BR', {
      weekday: 'long',
      day: '2-digit',
      month: 'long',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    }).format(new Date(date));
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-primary-dark">
        <Header />
        <Navigation />
        <div className="flex items-center justify-center py-20">
          <Card className="bg-card border-border p-8">
            <CardContent className="flex flex-col items-center gap-4">
              <div className="h-8 w-8 animate-spin rounded-full border-b-2 border-primary"></div>
              <p className="text-secondary-light">Carregando histÃ³rico de vendas...</p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-primary-dark">
      <Header />
      <Navigation />

      <main className="mobile-safe max-w-6xl py-4 sm:py-6 lg:py-8">
        {/* History Header */}
        <div className="flex justify-between items-start mb-6">
          <div className="flex items-center gap-3">
            <History className="text-info" size={32} />
            <div>
              <h2 className="text-2xl font-bold text-primary-light">HistÃ³rico de Vendas</h2>
              <p className="text-secondary-light">Acompanhe todas as vendas realizadas pela equipe</p>
            </div>
          </div>
          <Button className="bg-success text-primary-light hover:bg-success-dark">
            <Plus size={18} className="mr-2" />
            Exportar Dados
          </Button>
        </div>

        {/* Summary Statistics */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <Card className="bg-success/10 border-success/30">
            <CardContent className="p-6">
              <div className="flex items-center gap-3">
                <TrendingUp className="text-success" size={24} />
                <div>
                  <p className="text-2xl font-bold text-success">R$ {totalValue.toFixed(2)}</p>
                  <p className="text-sm text-secondary-light">Total Vendido</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-info/10 border-info/30">
            <CardContent className="p-6">
              <div className="flex items-center gap-3">
                <BarChart3 className="text-info" size={24} />
                <div>
                  <p className="text-2xl font-bold text-info">{totalSales}</p>
                  <p className="text-sm text-secondary-light">Vendas Realizadas</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card className="bg-warning/10 border-warning/30">
            <CardContent className="p-6">
              <div className="flex items-center gap-3">
                <DollarSign className="text-warning" size={24} />
                <div>
                  <p className="text-2xl font-bold text-warning">R$ {averageTicket.toFixed(2)}</p>
                  <p className="text-sm text-secondary-light">Ticket MÃ©dio</p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <Card className="mb-6 bg-card border-border">
          <CardContent className="p-6">
            <div className="flex items-center gap-3 mb-4">
              <Filter className="text-secondary-light" size={20} />
              <h3 className="text-lg font-semibold text-primary-light">Filtros de Pesquisa</h3>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div>
                <label className="text-sm font-medium text-secondary-light mb-2 block">
                  Filtrar por Atendente
                </label>
                <Select value={selectedAttendant} onValueChange={setSelectedAttendant}>
                  <SelectTrigger className="bg-input border-border text-primary-light">
                    <SelectValue placeholder="Todos os atendentes" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">Todos os atendentes</SelectItem>
                    {attendants?.map(attendant => (
                      <SelectItem key={attendant.id} value={attendant.id.toString()}>
                        {attendant.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium text-secondary-light mb-2 block">
                  Filtrar por MÃªs
                </label>
                <Select value={selectedMonth} onValueChange={setSelectedMonth}>
                  <SelectTrigger className="bg-input border-border text-primary-light">
                    <SelectValue placeholder="Todos os meses" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">Todos os meses</SelectItem>
                    <SelectItem value="01">Janeiro</SelectItem>
                    <SelectItem value="02">Fevereiro</SelectItem>
                    <SelectItem value="03">MarÃ§o</SelectItem>
                    <SelectItem value="04">Abril</SelectItem>
                    <SelectItem value="05">Maio</SelectItem>
